<?phpclass ye{	public $yid,$info;	public function __construct($yid){		GLOBAL $mysqli;		$this->yid=$yid;		$sql="SELECT * FROM `yec_ye` WHERE `yid`='".intval($yid)."';";		$rs=$mysqli->query($sql);		$row=$rs->fetch_row();		$field=$rs->fetch_fields();		for($i=1;$i<$mysqli->field_count-1;$i++){//-1 for disclaimer			if($i==4){				$this->info[$field[$i]->name]=stripslashes($row[$i]);				continue;			}			$this->info[$field[$i]->name]=decrypt(stripslashes($row[$i]));			//echo decrypt(stripslashes($row[$i]))."\n";		}        $this -> info['ydisclaimer']=$row[$i];	}}?>